pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

// https://docs.synthetix.io/contracts/source/contracts/owned
contract OwnedUpgradeable is Initializable {
    address public owner;
    address public nominatedOwner;

    function __Owned_init(address _owner) public initializer {
        require(_owner != address(0), "Owner address cannot be 0");
        owner = _owner;
        emit OwnerChanged(address(0), _owner);
    }

    function nominateNewOwner(address _owner) external onlyOwner {
        nominatedOwner = _owner;
        emit OwnerNominated(_owner);
    }

    function acceptOwnership() external {
        require(msg.sender == nominatedOwner, "You must be nominated before you can accept ownership");
        emit OwnerChanged(owner, nominatedOwner);
        owner = nominatedOwner;
        nominatedOwner = address(0);
    }

    modifier onlyOwner {
        _onlyOwner();
        _;
    }

    function _onlyOwner() private view {
        require(msg.sender == owner, "Only the contract owner may perform this action");
    }

    event OwnerNominated(address newOwner);
    event OwnerChanged(address oldOwner, address newOwner);
}","keccak256":"0x7a6570e90fb7c8ea550877d7c496eb48932a8eaf949c0b16dcf237cc78d0dde5","license":"MIT"},"contracts/utils/Pausable.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

// Inheritance
import "./OwnedUpgradeable.sol";

// https://docs.synthetix.io/contracts/source/contracts/pausable
abstract contract Pausable is OwnedUpgradeable {
    uint public lastPauseTime;
    bool public paused;

    function __Pausable_init(address _owner) public initializer {
        __Owned_init(_owner);
        require(owner != address(0), "Owner must be set");
    }

    /**
     * @notice Change the paused state of the contract
     * @dev Only the contract owner may call this.
     */
    function setPaused(bool _paused) external onlyOwner {
        // Ensure we're actually changing the state before we do anything
        if (_paused == paused) {
            return;
        }

        // Set our paused state.
        paused = _paused;

        // If applicable, set the last pause time.
        if (paused) {
            lastPauseTime = block.timestamp;
        }

        // Let everyone know that our pause state has changed.
        emit PauseChanged(paused);
    }

    event PauseChanged(bool isPaused);

    modifier notPaused {
        _notPaused();
        _;
    }

    function _notPaused() internal view {
        require(!paused, "This action cannot be performed while the contract is paused");
    }
}
